CREATE TABLE CRYPTOBOT_CONFIG (KEY VARCHAR(255) NOT NULL, VALUE VARCHAR(255) NOT NULL, PRIMARY KEY (KEY));
CREATE TABLE TRADEAKTION (TRADEAKTION_PK BIGINT NOT NULL, ERSTELLTAM TIMESTAMP NOT NULL, TRADE_STATUS VARCHAR(255) NOT NULL, PLATTFORM VARCHAR(255) NOT NULL, USERID BIGINT, VON_MENGE NUMERIC NOT NULL, VON_WAEHRUNG_FK VARCHAR(255) NOT NULL, ZU_MENGE NUMERIC NOT NULL, ZU_WAEHRUNG_FK VARCHAR(255) NOT NULL, PRIMARY KEY (TRADEAKTION_PK));
CREATE TABLE TRADEJOB (TRADEJOB_PK BIGINT NOT NULL, CRYPTOWAEHRUNG VARCHAR(255) NOT NULL, ERLEDIGTAM TIMESTAMP, ERSTELLTAM TIMESTAMP NOT NULL, JOB_STATUS VARCHAR(255) NOT NULL, KAUFWERT NUMERIC NOT NULL, LETZTWERT NUMERIC, MENGE NUMERIC NOT NULL, CRYPTOWAEHRUNG_REFERENZ VARCHAR(255) NOT NULL, TRADE_STATUS VARCHAR(255) NOT NULL, PLATTFORM VARCHAR(255) NOT NULL, ZIELWERT NUMERIC, PRIMARY KEY (TRADEJOB_PK));
CREATE TABLE WAEHRUNG (WAEHRUNG_PK BIGINT NOT NULL, BEZEICHNUNG VARCHAR(255) NOT NULL, GUELTIG CHAR(1) NOT NULL, KURZBEZEICHNUNG VARCHAR(255) NOT NULL, PRIMARY KEY (WAEHRUNG_PK));
CREATE TABLE WECHSEL_TRADEJOB (WECHSEL_TRADEJOB_PK BIGINT NOT NULL, GANZ_ZAHLIG CHAR(1) NOT NULL, KAUFWERT_GRENZE NUMERIC NOT NULL, MENGE_REFERENZWERT NUMERIC NOT NULL, MIN_ZIELSATZ NUMERIC, TRADETYP VARCHAR(255) NOT NULL, TRADEVERSUCHAM TIMESTAMP, VORGESEHNER_VERKAUFSWERT NUMERIC, PRIMARY KEY (WECHSEL_TRADEJOB_PK));
